const Dropdown = () => {
    return (
        <DropdownContainer>
          <Icon>
              <CloseIcon/>
          </Icon>
          <DropdownWrapper>
              <DropdownMenu>
                  {menuData.map((item,index)=>{
                      <DropdownLink to={item.link} key={index}>
                            {item.title}
                      </DropdownLink>
                  })}
              </DropdownMenu>
              <BtnWrap>
                  <Button primary="true" round="true" big="true" to="/PartyRegistration">
                      Party Registration
                  </Button>
             </BtnWrap>
          </DropdownWrapper>
        </DropdownContainer>
    )
}

export default Dropdown
